            for i in form:
                self.submenu = self.second.addMenu(i)
                self.submenu.setTitle(i)
                table = self.get_table_from_form(i)
                for j in table:
                    self.action = self.submenu.addAction(j)
                    self.action.setText(j)
                    self.action.triggered.connect(self.menu_decHardware)
